import React from 'react';

 const ButtonPress = (route) => {
    fetch(`https://swapi.co/api/${route}`) // fetch is a function of window, it fetches a json object
      .then(response => response.json())
      .then(data => this.setState( {array: data.results, selected: route} ));

	return(

		<button onClick={this.ButtonPress('People')}> People</button>

		)
}

export default Buttons
