import React, { Component } from 'react';
import './App.css';
import PeopleList from './PeopleList';


class App extends Component {

   constructor() {
    super()
    this.state = {
      array: [],

    }
  }

  componentDidMount() {
    fetch('https://swapi.co/api/people')
      .then(response=> response.json())
      .then(heros => {this.setState({array: object.key(heros)})});

  }

  render() {
    const array = this.state;
    const filteredArray = array.filter(hero =>{
      return hero.name.toLowerCase();
    })
    return (
    <PeopleList array = {filteredArray} />
    );

}}
export default App;
