import React, { Component } from 'react';
import './App.css';


class App extends Component {

   constructor() {
    super()
    this.state = {
      Hero: [],

    }
  }

  componentDidMount() {
    fetch('http https://swapi.co/api/people')
      .then(response=> response.json())
      .then(people => {this.setState({Hero:people})});

  }
  render() {


    const Hero = this.state;
    return (
       <React.Fragment>
        {Hero}
    </React.Fragment>
    );
  }
}


export default App;
