import React, { Component } from 'react';
import './App.css';
import Button from './Button';
import Results from './Results';

class App extends Component {

   constructor() {
    super()
    this.state = {
      results: [],

    }
  }

  componentDidMount() {
    fetch('http https://swapi.co/api/people/')
      .then(response=> response.json())
      .then(people => {this.setState({results:people})});
  }
  render() {
    const results = this.state;
    return (
      <div className="App">

        <Button/>
        <Results results={results}/>

      </div>
    );
  }
}


export default App;
