import React, { Component } from 'react';
import PeopleList from './PeopleList';
import Button from './Buttons';

class App extends Component {

   constructor() {
    super()
    this.state = {
      array: [],
      selected: '',
    }
  }

  ButtonPress = (route) => {
    fetch(`https://swapi.co/api/${route}`) // fetch is a function of window, it fetches a json object
      .then(response => response.json())
      .then(data => this.setState( {array: data.results, selected: route} ));
  }



  render() {
    const array = this.state.array // we want to be able to pass the state ( in this case the array of data) to our PeopleList, so we create a variable.
    console.log(array);



    return (

    <div>
    <h1 ClassName = ""> Star Wars </h1>
    <h2 ClassName = ""  > May the force be with you....</h2>
    <Button ButtonPress = {ButtonPress}/>
    <PeopleList array = {array}  /> 
    </div>
    //we pass these variables as a PROPs to the PeopleList Card, Buttons etc, so that they can be used in those componants
    );

}}
export default App;
